/**
 * =========================
 * PIPELINE: Smartsheet ‚Üí Google Sheet ‚Üí GitHub CSV
 * =========================
 * This script:
 *  1. Imports data from a Smartsheet Report
 *  2. Cleans, normalizes, and enriches the data for REACH
 *  3. Uploads the final CSV into a GitHub repository for use in the REACH UI
 * 
 * Each helper function handles a specific step in the pipeline.
 */

/**
 * Import data from a Smartsheet report into the "Open Science Report" sheet.
 * - Paginates through Smartsheet API (500 rows at a time)
 * - Clears old data
 * - Writes fresh headers and row content
 */

function importSmartsheetReport() {
  const baseUrl = 'https://api.smartsheetgov.com/2.0/reports/ENTER REPORT ID'; // Make sure to add the Smartsheet ID from the report you are pulling in
  const pageSize = 500;
  const smartsheetToken = PropertiesService.getScriptProperties().getProperty('SMARTSHEET_TOKEN'); // Make sure to add your Smartsheet token to the settings under "Script Properties"
  const token = `Bearer ${smartsheetToken}`; // Replace with real token

  const headers = {
    'Authorization': token
  };

  let allRows = [];
  let columns = null;
  let page = 1;

// Loop through Smartsheet report until all rows are retrieved
  while (true) {
    const url = `${baseUrl}?pageSize=${pageSize}&page=${page}`;
    const response = UrlFetchApp.fetch(url, {
      method: 'get',
      headers: headers,
      muteHttpExceptions: true
    });

    const json = JSON.parse(response.getContentText());

    if (!json || !json.rows || json.rows.length === 0) {
      break; // No more rows
    }

    if (!columns) {
      columns = json.columns;
    }

    allRows = allRows.concat(json.rows);

    if (json.rows.length < pageSize) {
      break; // Last page
    }

    page++;
  }

  const sheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName("Open Science Report");
  if (!sheet) throw new Error('Sheet "Open Science Report" not found');

  // Clear all existing data below headers
  const lastRow = sheet.getLastRow();
  if (lastRow > 1) {
    sheet.getRange(2, 1, lastRow - 1, sheet.getLastColumn()).clearContent();
  }

  // Write header row from Smartsheet column titles
  const headerTitles = columns.map(col => col.title);
  sheet.getRange(1, 1, 1, headerTitles.length).setValues([headerTitles]);

  // Map rows into spreadsheet-friendly format
  const data = allRows.map(row => {
  const cellMap = {};
  row.cells.forEach(cell => {
    let value = cell.displayValue || cell.value || '';

      // Normalize date strings ‚Üí MM/DD/YY
    if (typeof value === 'string' && value.match(/^\w{3} \w{3} \d{2,4}/)) {
      const date = new Date(value);
      if (!isNaN(date)) {
        value = Utilities.formatDate(date, Session.getScriptTimeZone(), 'MM/dd/yy');
      }
    }

    cellMap[cell.virtualColumnId] = value;
  });

  return columns.map(col => cellMap[col.virtualId] || '');
});

  // Write rows in chunks to avoid "too many rows" API errors
  const chunkSize = 100;
  for (let i = 0; i < data.length; i += chunkSize) {
    const chunk = data.slice(i, i + chunkSize);
    sheet.getRange(i + 2, 1, chunk.length, headerTitles.length).setValues(chunk);
  }

  Logger.log(`Successfully wrote ${data.length} rows.`);
}

/**
 * Reformat all dates in column M into MM/DD/YYYY format.
 * Smartsheet exports inconsistent date formats, this standardizes them.
 */

function reformatDatesInColumnM() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName("Open Science Report");
  const colIndex = 13; // Column M
  const numRows = sheet.getLastRow();

  if (numRows < 2) return; // No data to process

  const range = sheet.getRange(2, colIndex, numRows - 1, 1); // From row 2 down, one column wide
  const values = range.getValues();

  const formatted = values.map(([cell]) => {
    if (!cell) return [""]; // Leave blank cells empty

    if (Object.prototype.toString.call(cell) === "[object Date]") {
      return [Utilities.formatDate(cell, Session.getScriptTimeZone(), "MM/dd/yyyy")];
    }

    if (typeof cell === "string" && cell.includes("GMT")) {
      const parsed = new Date(cell);
      if (!isNaN(parsed)) {
        return [Utilities.formatDate(parsed, Session.getScriptTimeZone(), "MM/dd/yyyy")];
      }
    }

    return [cell]; // Leave unchanged if not a recognized date
  });

  range.setValues(formatted);
  sheet.getRange(2, colIndex, numRows - 1, 1).setValues(formatted);
  sheet.getRange(2, colIndex, numRows - 1, 1).setNumberFormat("MM/dd/yyyy");
}

/**
 * Generate a combined search string (title + abstract) for REACH keyword search.
 * Writes combined text into column W.
 */

function generateSearchColumn() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName("Open Science Report");
  if (!sheet) throw new Error('Sheet "Open Science Report" not found');

  const lastRow = sheet.getLastRow();
  if (lastRow < 2) return;

  // Read J (10) + K (11) unchanged
  const titleAbstractData = sheet.getRange(2, 10, lastRow - 1, 2).getValues();

  const combinedSearchValues = titleAbstractData.map(row => {
    const title = row[0] || "";
    const abstract = row[1] || "";
    const combined = [title, abstract]
      .map(v => v.toString().toLowerCase().trim())
      .filter(v => v !== "")
      .join(", ");
    return [combined];
  });

  // Write to W (23)
  sheet.getRange(2, 23, combinedSearchValues.length, 1).setValues(combinedSearchValues);
}

/**
 * Truncate abstracts (column K) to 45 words for UI card preview.
 * Writes results into column X.
 */

function truncateBriefAbstract() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName("Open Science Report");
  if (!sheet) throw new Error('Sheet "Open Science Report" not found');

  const lastRow = sheet.getLastRow();
  if (lastRow < 2) return;

  // Read K (11) unchanged
  const abstractData = sheet.getRange(2, 11, lastRow - 1, 1).getValues();

  const truncatedAbstracts = abstractData.map(row => {
    const abstract = row[0] || "";
    const words = abstract.split(/\s+/);
    const truncated = words.slice(0, 45).join(" ");
    const finalText = words.length > 45 ? truncated + "..." : truncated;
    return [finalText];
  });

  // Write to X (24)
  sheet.getRange(2, 24, truncatedAbstracts.length, 1).setValues(truncatedAbstracts);
}

/**
 * Combine multiple link columns (N‚ÄìQ) into a single field with newline separators.
 * Writes results into column Y.
 */

function combineLinksIntoOneColumn() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getActiveSheet();
  const lastRow = sheet.getLastRow();
  if (lastRow < 2) return;

  // Read N (14) ‚Üí Q (17) unchanged
  const linkData = sheet.getRange(2, 14, lastRow - 1, 4).getValues();

  const combinedLinks = linkData.map(row => {
    const nonEmpty = row.filter(link => link && link.toString().trim() !== "");
    return [nonEmpty.join('\n')];
  });

  // Write to Y (25)
  sheet.getRange(2, 25, combinedLinks.length, 1).setValues(combinedLinks);
}

/**
 * Convert numeric output type codes (1‚Äì4) into HTML with icons + labels.
 * Writes results into column Z.
 */

function formatOutputTypeWithIcons() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getActiveSheet();
  const lastRow = sheet.getLastRow();
  if (lastRow < 2) return;

  const outputTypeValues = sheet.getRange(2, 1, lastRow - 1, 1).getValues(); // A (1)

  const htmlFormatted = outputTypeValues.map(row => {
    const value = row[0];
    let result = "Unknown";
    switch (value) {
      case 1: result = "<span class='material-icons' style='font-size: 20px; vertical-align: middle;'>library_books</span> <span style='vertical-align: middle; line-height: 1.5;'>Article</span>"; break;
      case 2: result = "<span class='material-icons' style='font-size: 20px; vertical-align: middle;'>terminal</span> <span style='vertical-align: middle; line-height: 1.5;'>Code Release</span>"; break;
      case 3: result = "<span class='material-icons' style='font-size: 20px; vertical-align: middle;'>storage</span> <span style='vertical-align: middle; line-height: 1.5;'>Available Data</span>"; break;
      case 4: result = "<span class='material-icons' style='font-size: 20px; vertical-align: middle;'>folder_open</span> <span style='vertical-align: middle; line-height: 1.5;'>Other Output</span>"; break;
    }
    return [result];
  });

  // Write to Z (26)
  sheet.getRange(2, 26, htmlFormatted.length, 1).setValues(htmlFormatted);
}

/**
 * Assign color codes to card headers based on output type.
 * Writes results into column AA.
 */

function assignCardHeaderColors() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getActiveSheet();
  const lastRow = sheet.getLastRow();
  if (lastRow < 2) return;

  const outputTypes = sheet.getRange(2, 1, lastRow - 1, 1).getValues(); // A (1)

  const colorCodes = outputTypes.map(row => {
    const type = row[0];
    let color = "#ffffff";
    switch (type) {
      case 1: color = "#cce5ff"; break;
      case 2: color = "#ffe6cc"; break;
      case 3: color = "#d4edda"; break;
      case 4: color = "#f0c1f7"; break;
    }
    return [color];
  });

  // Write to AA (27)
  sheet.getRange(2, 27, colorCodes.length, 1).setValues(colorCodes);
}

/**
 * Assign color codes to link text based on output type.
 * Writes results into column AB.
 */

function assignLinkColors() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getActiveSheet();
  const lastRow = sheet.getLastRow();
  if (lastRow < 2) return;

  const outputTypes = sheet.getRange(2, 1, lastRow - 1, 1).getValues(); // A (1)

  const colorCodes = outputTypes.map(row => {
    const type = row[0];
    let color = "#ffffff";
    switch (type) {
      case 1: color = "#1a73e8"; break;
      case 2: color = "#f68c1f"; break;
      case 3: color = "#28a745"; break;
      case 4: color = "#e56af7"; break;
    }
    return [color];
  });

  // Write to AB (28)
  sheet.getRange(2, 28, colorCodes.length, 1).setValues(colorCodes);
}

/**
 * Assign descriptive link text (e.g. "View Full Publication") with icons.
 * Writes results into column AC.
 */

function assignLinkText() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getActiveSheet();
  const lastRow = sheet.getLastRow();
  if (lastRow < 2) return;

  const outputTypes = sheet.getRange(2, 1, lastRow - 1, 1).getValues(); // A (1)

  const labels = outputTypes.map(row => {
    const type = row[0];
    let label = "Unknown";
    switch (type) {
      case 1: label = "<span class='material-icons' style='font-size: 20px; vertical-align: middle;'>link</span> <span style='vertical-align: middle; line-height: 1.5;'>View Full Publication</span>"; break;
      case 2: label = "<span class='material-icons' style='font-size: 20px; vertical-align: middle;'>link</span> <span style='vertical-align: middle; line-height: 1.5;'>View Code Release</span>"; break;
      case 3: label = "<span class='material-icons' style='font-size: 20px; vertical-align: middle;'>link</span> <span style='vertical-align: middle; line-height: 1.5;'>View Available Data</span>"; break;
      case 4: label = "<span class='material-icons' style='font-size: 20px; vertical-align: middle;'>link</span> <span style='vertical-align: middle; line-height: 1.5;'>View Other Outputs</span>"; break;
    }
    return [label];
  });

  // Write to AC (29)
  sheet.getRange(2, 29, labels.length, 1).setValues(labels);
}

/**
 * Add open access labels:
 * - ‚ÄúAvailable on NOAA IR‚Äù for articles in NOAA IR
 * - ‚ÄúPublicly Available‚Äù for other outputs
 * Writes results into column AD.
 */

function assignOpenAccessLabel() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getActiveSheet();
  const lastRow = sheet.getLastRow();
  if (lastRow < 2) return;

  const irValues = sheet.getRange(2, 6, lastRow - 1, 1).getValues(); // F (6)
  const outputTypes = sheet.getRange(2, 1, lastRow - 1, 1).getValues(); // A (1)

  const labels = irValues.map((row, i) => {
    const ir = row[0];
    const type = outputTypes[i][0];

    if ((ir === true || ir === "TRUE") && type === 1) {
      return ["Available on<br>NOAA IR"];
    } else if (type === 2 || type === 3 || type === 4) {
      return ["Publicly<br> Available"];
    } else {
      return [""];
    }
  });

  // Write to AD (30)
  sheet.getRange(2, 30, labels.length, 1).setValues(labels);
}

/**
 * Add open access icons (cloud_done) to outputs.
 * Writes results into column AE.
 */

function assignOpenAccessIcon() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getActiveSheet();
  const lastRow = sheet.getLastRow();
  if (lastRow < 2) return;

  const irValues = sheet.getRange(2, 6, lastRow - 1, 1).getValues(); // F (6)
  const outputTypes = sheet.getRange(2, 1, lastRow - 1, 1).getValues(); // A (1)

  const labels = irValues.map((row, i) => {
    const ir = row[0];
    const type = outputTypes[i][0];

    if ((ir === true || ir === "TRUE") && type === 1) {
      return ["<span class='material-icons'>cloud_done</span>"];
    } else if (type === 2 || type === 3 || type === 4) {
      return ["<span class='material-icons'>cloud_done</span>"];
    } else {
      return [""];
    }
  });

  // Write to AE (31)
  sheet.getRange(2, 31, labels.length, 1).setValues(labels);
}

/**
 * Normalize values in the "Funded By" column.
 * Maps acronyms (e.g. JTTI, EPIC, OBS) into full program names.
 * Writes cleaned values back into column I.
 */

function updateFundedByColumn() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName("Open Science Report"); // Change to your sheet name
  const range = sheet.getRange(2, 9, sheet.getLastRow() - 1); // Row 2 down, column 9 (I)
  const values = range.getValues();

  const testbedAcronyms = ["JHT", "HMT", "HWT", "CTB", "USWRP", "FWT", "HOT", "USWRP R2O", "Testbeds-HWT"]; // Add others as needed

  for (let i = 0; i < values.length; i++) {
    let cell = values[i][0];
    if (!cell) continue;

    const trimmed = cell.toString().trim().toUpperCase();

    if (trimmed === "JTTI") {
      values[i][0] = "Joint Technology Transfer Initiative (JTTI)";
    } else if (trimmed === "JTTI-STI") {
      values[i][0] = "Joint Technology Transfer Initiative (JTTI)";
    } else if (trimmed === "EPIC") {
      values[i][0] = "Earth Prediction Innovation Center (EPIC)";
    } else if (trimmed === "OBS") {
      values[i][0] = "Observations";
    } else if (trimmed === "SPSM") {
      values[i][0] = "Observations";
    } else if (trimmed === "APAR") {
      values[i][0] = "Observations";
    } else if (trimmed === "IS") {
      values[i][0] = "Observations";
    } else if (trimmed === "AQRF") {
      values[i][0] = "Synoptic";
    } else if (trimmed === "FIREWX") {
      values[i][0] = "Synoptic";
    } else if (trimmed === "S2S") {
      values[i][0] = "Subseasonal to Seasonal (S2S)";
    } else if (trimmed === "CLIMATE SLA") {
      values[i][0] = "Subseasonal to Seasonal (S2S)";
    } else if (trimmed === "ESPC") {
      values[i][0] = "Subseasonal to Seasonal (S2S)";
    } else if (trimmed === "INNOVATIONS") {
      values[i][0] = "Subseasonal to Seasonal (S2S)";  
    } else if (testbedAcronyms.includes(trimmed)) {
      values[i][0] = "Testbeds";
    }
  }

  range.setValues(values);
}

/**
 * Upload the cleaned sheet to GitHub as a CSV.
 * - Sanitizes text for safe CSV export
 * - Encodes CSV as Base64
 * - Pushes file via GitHub API (creates or updates)
 */

function uploadSheetToGitHub() {
  const sheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName("Open Science Report");
  const range = sheet.getDataRange();
  const values = range.getValues();

  // Helper to clean problematic characters that often become ?
  function sanitizeForCSV(text) {
    if (typeof text !== 'string') return text;
    return text
      .replace(/\u2013|\u2014|\u2012|\u2011/g, "-") // en dash, em dash, etc.
      .replace(/[\u2018\u2019]/g, "'")             // curly apostrophes
      .replace(/[\u201C\u201D]/g, '"')             // curly quotes
      .replace(/\u00A0/g, " ")                     // non-breaking space
      .replace(/\uFFFD/g, "")                      // ÔøΩ replacement character
      .replace(/[^\x00-\x7F]/g, "");               // remove any remaining non-ASCII
  }

  // Convert to sanitized CSV
  const csv = values.map(row =>
    row.map(cell => {
      const clean = sanitizeForCSV(String(cell));
      return `"${clean.replace(/"/g, '""')}"`;
    }).join(",")
  ).join("\n");

  // Encode CSV to Base64
  const base64csv = Utilities.base64Encode(csv);

  // GitHub info
  const repo = 'INSERT YOUR REPOSITORY';       
  const path = 'INSERT YOUR PATH';
  const branch = 'main';                        
  const githubToken = PropertiesService.getScriptProperties().getProperty('GITHUB_TOKEN'); //Insert Your GITHUB_TOKEN into the Script Properties in Google App Script settings

  // Get current SHA (if file exists, needed for updates)
  const getUrl = `https://api.github.com/repos/${repo}/contents/${path}`;
  const getResponse = UrlFetchApp.fetch(getUrl, {
    method: 'get',
    headers: {
      Authorization: `Bearer ${githubToken}`,
      Accept: 'application/vnd.github.v3+json'
    },
    muteHttpExceptions: true
  });

  let sha = null;
  if (getResponse.getResponseCode() === 200) {
    const getJson = JSON.parse(getResponse.getContentText());
    sha = getJson.sha;
  }

  // Push new content
  const payload = {
    message: `Update CSV export from Google Sheet`,
    content: base64csv,
    branch: branch
  };
  if (sha) payload.sha = sha;

  const putUrl = `https://api.github.com/repos/${repo}/contents/${path}`;
  const putResponse = UrlFetchApp.fetch(putUrl, {
    method: 'put',
    headers: {
      Authorization: `Bearer ${githubToken}`,
      Accept: 'application/vnd.github.v3+json'
    },
    contentType: 'application/json',
    payload: JSON.stringify(payload)
  });

  const result = JSON.parse(putResponse.getContentText());
  Logger.log(`CSV uploaded: ${result.content.download_url}`);
}

/**
 * Set a monthly recurring trigger (typically the 15th of every month to automatically run the entire pipeline)
 * Run the full ETL pipeline:
 *  1. Import data from Smartsheet
 *  2. Clean and format
 *  3. Enrich metadata
 *  4. Upload to GitHub
 */

function runFullPipeline() {
  importSmartsheetReport();         // Step 1: Get data from Smartsheet
  reformatDatesInColumnM();            // Step 2: Format dates
  generateSearchColumn();           // Step 3: Create search string
  truncateBriefAbstract();          // Step 4: Shorten abstract
  combineLinksIntoOneColumn();      // Step 5: Combine link columns
  formatOutputTypeWithIcons();      // Step 6: Add output type icons
  assignCardHeaderColors();         // Step 7: Assign header colors
  assignLinkColors();               // Step 8: Assign link colors
  assignLinkText();                 // Step 9: Assign link display text
  assignOpenAccessLabel();          // Step 10: Add open access labels
  assignOpenAccessIcon();           // Step 11: Add open access icons
  updateFundedByColumn();           // Step 12: Clean and update Funded By column
  uploadSheetToGitHub();            // Step 13: Uploaded new CSV to GitHub

  Logger.log("üéâ Full pipeline completed successfully.");
}
